import React , {useState} from 'react'
import logo from '../images/me2.png';
import plogo from '../images/p-logo.png'
import clogo from '../images/c-logo.png'
import scriptlogo from '../images/script-logo.png'
import react from '../images/react.png'
import "./about.scss"
import {useSpring, animated} from 'react-spring';
import { Spring } from 'react-spring/renderprops';
import barPers from "./utils";

class About extends React.Component {
     isInViewport = (element) => {
        var box = document.querySelector('.skills'); 
        const rect = element.getBoundingClientRect();
        return (
            rect.top >= 0 &&
            rect.left >= 0 &&
            rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
            rect.right <= (window.innerWidth || document.documentElement.clientWidth)
        );
    }

    /*constructor(props) {
      super(props);
          parseInt()  this.state = {
        linkselected: 0,
        language: 1};
      }*/
    //   const per = useSpring({ number: 90, from: { number: 0 }, })
    //   const number = parseInt(per.number).toString;

  render(){
  return (
     <div className="about-container"> 

         {/* <h2>my Skills</h2> */}
         <div className="skills">
            <div className="item">
            <div className="skill-title">
                <h5 className="skill-name">C Language</h5>

                </div>
                <Spring delay={2000}     from={  { percent: 0 }} to={{ percent: 90}}>
            {({ percent }) => (

              <div className="bar">
                <div style={{ width: `${percent}%` }} className="ii">         
                     <span className="pers">{`${percent.toFixed()}%`}</span>
                </div>
              </div>
            )}
          </Spring>
                <div className="logo-img" >
                    <img src={clogo} alt="mok"/> 
                </div>
            </div>
            <div className="item">
            <div className="skill-title">
                <h5 className="skill-name">Python</h5>
                </div>
                <Spring delay={2000}     from={  { percent: 0 }} to={{ percent: 80}}>
            {({ percent }) => (

              <div className="bar">
                <div style={{ width: `${percent}%` }} className="ii">         
                     <span className="pers">{`${percent.toFixed()}%`}</span>
                </div>
              </div>
            )}
          </Spring>
                <div className="logo-img" >
                    <img src={plogo} alt="mok"/> 
                </div>
            </div>
            <div className="item">
                <div className="skill-title">
                <h5 className="skill-name">Scripting</h5>
                     <span className="pers">75%</span>
                          </div>
                <Spring delay={2000}     from={  { percent: 0 }} to={{ percent: 75}}>
            {({ percent }) => (

              <div className="bar">
                <div style={{ width: `${percent}%` }} className="ii">         
                     <span className="pers">{`${percent.toFixed()}%`}</span>
                </div>
              </div>
            )}
          </Spring>
                <div className="logo-img" >
                    <img src={scriptlogo} alt="mok"/> 
                </div>
            </div>
            <div className="item">
            <div className="skill-title" id="ee">
                <h5 className="skill-name"  >React (HTML && CSS)</h5>
                </div>
                <Spring delay={2000}     from={  { percent: 0 }} to={{ percent: 90}}>
            {({ percent }) => (

              <div className="bar">
                <div style={{ width: `${percent}%` }} className="ii">         
                     <span className="pers">{`${percent.toFixed()}%`}</span>
                </div>
              </div>
            )}
          </Spring>
                <div className="logo-img" id="exx">
                <i class="fab fa-react"></i>
                </div>
            </div>

         </div>
         <div className="image-desc">
            <div className="image-2">
                <img src={logo} alt="mok"/> 
            </div>
            <div className="desc">
                <h2>About me</h2>
                <p>It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. </p>
                <p>IContrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur</p>
            </div>
         </div>
    </div>
  );
}

}

export default About;
