import React , {useState} from 'react'


import './navbar.scss';

// import {auth} from '../../firbase'

class Navbar extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        linkselected: 0,
        language: 1};
      }

    changelink (type) 
    {
        this.setState({linkselected:type});
    }
    changelanguage (type) 
    {
        this.setState({language:type});
    }

 render(){   
  return (
    <div className="navbar-conrtainer">
        <div className="logo">
            <h1>LOGO</h1>
        </div>
        <ul className="pages-list">  
                <li >  <a onClick={()=>this.changelink(0)}
                href=""
                className= {this.state.linkselected == 0  ? "link-active" : ""}
                 >HOME
                 </a></li> 
                <li> <a  onClick={()=>this.changelink(1)}
                className= {this.state.linkselected == 1  ? "link-active" : ""} 
                 >ABOUT</a>
                </li>
                <li> <a onClick={()=>this.changelink(2)}
                className= {this.state.linkselected == 2  ? "link-active" : ""}
                >SERVICE
                </a></li>
        </ul>
        <ul className="language-select">
            <li> <a onClick={()=>this.changelanguage(0)}
            className = {this.state.language == 0  ? "link-active" : ""}>FR</a> </li>
            <li> <a onClick={()=>this.changelanguage(1)}
            className = {this.state.language == 1  ? "link-active" : ""}>ENG</a> </li>
        </ul>
    </div>
  );
}
}


export default Navbar;
